import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";

function InventarioA() {
  const [productos, setProductos] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/inventario');
        if (!response.ok) {
          throw new Error('Error al obtener productos');
        }
        const data = await response.json();
        setProductos(data);
      } catch (err) {
        console.error(err);
        setError('No se pudo cargar el inventario');
      }
    };

    fetchProductos();
  }, []);

  return (
   <>
   <nav>
        <div>
            <ul className="nav-menu">
                <li className="nav-item">
                <Link to={'/vivians/inicio'}>Cerrar sesion</Link>
                </li>
                <li className="nav-item">
                <Link to={'/vivians/admin'}>Inventario</Link>
                </li>
                <li className="nav-item">
                <Link to={'/vivians/admin/users'}>Gestion de usuarios</Link>
                </li>
            </ul>
        </div>
    </nav>
    <main>
      <section>
        <h2>Inventario</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}

        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ccc', padding: '8px' }}>Nombre</th>
              <th style={{ border: '1px solid #ccc', padding: '8px' }}>Cantidad</th>
              <th style={{ border: '1px solid #ccc', padding: '8px' }}>Precio</th>
              <th style={{ border: '1px solid #ccc', padding: '8px' }}>Categor√≠a</th>
            </tr>
          </thead>
          <tbody>
            {productos.map((producto) => (
              <tr key={producto._id}>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{producto.nombre}</td>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{producto.cantidad}</td>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{producto.precio}</td>
                <td style={{ border: '1px solid #ccc', padding: '8px' }}>{producto.categoria}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
    
   </>
  );
}

export default InventarioA;
