import React, { useEffect, useState } from 'react';
import { link } from '../../../backend/rutas/invetarioRutas';

function Notificaciones() {
  const [notificaciones, setNotificaciones] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchNotificaciones = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/notificaciones');
        if (!response.ok) {
          throw new Error('Error al obtener notificaciones');
        }
        const data = await response.json();
        setNotificaciones(data);
      } catch (err) {
        console.error(err);
        setError(err.message || 'Error al conectar con el servidor');
      }
    };

    fetchNotificaciones();
  }, []);

  return (
    <>
    <nav>
        <div>
            <ul className="nav-menu">
                <li className="nav-item">
                <Link to={'/vivians/inicio'}>Cerrar sesion</Link>
                </li>
                <li className="nav-item">
                <Link to={'/vivians/admin'}>Inventario</Link>
                </li>
                <li className="nav-item">
                <Link to={'/vivians/admin/users'}>Gestion de usuarios</Link>
                </li>
                <li className="nav-item">
                <Link to={'/vivians/admin/notificaciones'}>Notificaciones</Link>
                </li>
            </ul>
        </div>
    </nav>
    <main>
    <section style={{ maxWidth: '800px', margin: 'auto', padding: '1rem' }}>
      <h2 style={{ textAlign: 'center' }}>Notificaciones de Stock Bajo</h2>

      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

      {notificaciones.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No hay notificaciones</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ background: '#eee' }}>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Producto</th>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Mensaje</th>
              <th style={{ border: '1px solid #ccc', padding: '0.5rem' }}>Fecha</th>
            </tr>
          </thead>
          <tbody>
            {notificaciones.map((n) => (
              <tr key={n._id}>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>
                  {n.productoId?.nombre || 'N/A'}
                </td>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>
                  {n.mensaje}
                </td>
                <td style={{ border: '1px solid #ccc', padding: '0.5rem' }}>
                  {new Date(n.fecha).toLocaleString()} {/* muestra fecha legible */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </section>
    </main>
    </>
  );
}

export default Notificaciones;
